tinymce.PluginManager.add("fpcm_emoticons", function (editor, t) {
    function getSmileyList() {
        var returnValue;
        
        jQuery.ajax({
            url: "../inc/lib/tinymce4/plugins/fpcm_emoticons/emoticons.php",
            type: "POST",
            async: false,
            success: function(data) {
                console.log(data);
                
                returnValue = data;              
            }
        });
        return returnValue;
    }

    editor.addButton("emoticons", {
        type: "panelbutton",
        popoverAlign: "bc-tl",
        tooltip: "Emoticons",
        panel: {
            autohide: true,
            classes: 'fpcm_smileys',
            html: getSmileyList,
            onclick: function(e) {
                var linkElm = editor.dom.getParent(e.target, 'img');
                if (linkElm) {
                    editor.insertContent(' ' + linkElm.getAttribute('alt') + ' ');
                    this.hide();
                }
            }
        }               
    });    
});