tinymce.PluginManager.add('fpcm_readmore', function(editor, url) {
	editor.addButton('fpcm_readmore', {
		icon: "pagebreak",
                tooltip: "readmore block",
		onclick: function() {
                    var newtext = prompt("Enter the text","");
                    if(newtext) editor.insertContent('<readmore>' + newtext + '</readmore>');
		}
	});
});